<script lang="ts">
	let typed = "3.14";
	let pi =
		"3.";
	let mistakes = 0;
	let score = 0;
	let i = 4;
	let highScore = +(localStorage.getItem("highScore") ?? 0);

	const startGame = () => {
		i = 4;
		score = 0;
		mistakes = 0;
	};

	const keydown = (e: KeyboardEvent) => {
		if (e.metaKey || isNaN(+e.key) || +e.key < 0 || +e.key > 9) return;
		if (pi[i] === e.key) {
			typed += e.key;
			score++;
			i++;
		} else {
			mistakes++;
		}
	};
</script>

<svelte:body on:keydown={keydown} />

<div class="flex flex-col items-center justify-center gap-24 py-24">
	<div class="flex w-full gap-10 text-3xl text-fuchsia-700/50">
		<div class="flex-1 text-right">{mistakes} mistakes</div>
		<div class="w-24 text-center text-6xl">{score}</div>
		<div class="flex-1 text-left">High score: {highScore}</div>
	</div>
	<div class="relative">
		<div
			class="glow text-bold font-mono absolute top-0 right-0 bg-red-500/0 text-5xl tracking-widest text-fuchsia-300"
		>
			{typed}
		</div>
	</div>
</div>
